mapper.select:
select t.* from ( select t1.*, '个人' as teamName from t_okr_manage_objectives t1 where t1.del_flag='0' and t1.type = '1' and t1.owner_id = #{userId}
union all select t1.*,t3.name as teamName from t_okr_manage_objectives t1 left join t_okr_team_user_rela t2 on t2.team_id=t1.team_id left join t_okr_teams t3 on t3.id=t2.team_id where t1.del_flag='0' and t1.type='2' and t2.user_id= #{userId}
union all select t1.*,t2.name as teamName from t_okr_manage_objectives t1 left join t_okr_teams t2 on t2.id=t1.team_id where t1.del_flag='0' and t1.type='3' and t2.type='1' ) t where 1 = 1
and t.time_session_id = #{timeSessionId}
order by t.create_ts desc
limit #{limitAmount}

select distinct t1.*, p.name as "parentName"
from t_okr_manage_objectives t1
left join t_okr_manage_results t2 on t2.object_id=t1.id and t2.del_flag='0'
left join t_okr_manage_objectives p on p.id = t1.parent_id
where t1.del_flag='0' and t1.type = '1' and t1.owner_id = #{userId}

    AND (
       t1.name LIKE CONCAT('%', #{keyword}, '%')
    or t1.description LIKE CONCAT('%', #{keyword}, '%')
    or t2.name LIKE CONCAT('%', #{keyword}, '%')
    or t2.description LIKE CONCAT('%', #{keyword}, '%')
    )


    AND t2.status = #{executeStatus}


    AND t1.id = #{objectId}


    and t1.time_session_id = #{timeSessionId}

order by t1.create_ts desc

select distinct t1.*,t3.name as "teamName", p.name as "parentName"
from t_okr_manage_objectives t1
left join t_okr_team_user_rela t2 on t2.team_id=t1.team_id
left join t_okr_teams t3 on t3.id=t2.team_id
left join t_okr_manage_results t4 on t4.object_id=t1.id and t4.del_flag='0'
left join t_okr_manage_objectives p on p.id = t1.parent_id
where t1.del_flag='0' and t1.type='2' and t2.user_id= #{userId}

    AND t1.team_id = #{teamId}


    AND (
       t1.name LIKE CONCAT('%', #{keyword}, '%')
    or t1.description LIKE CONCAT('%', #{keyword}, '%')
    or t4.name LIKE CONCAT('%', #{keyword}, '%')
    or t4.description LIKE CONCAT('%', #{keyword}, '%')
    )


    AND t4.status = #{executeStatus}


    AND t1.id = #{objectId}


    and t1.time_session_id = #{timeSessionId}

order by t1.create_ts desc

select distinct t1.*,t2.name as "teamName", p.name as "parentName" from t_okr_manage_objectives t1 left join t_okr_teams t2 on t2.id=t1.team_id left join t_okr_manage_results t4 on t4.object_id=t1.id and t4.del_flag='0' left join t_okr_manage_objectives p on p.id = t1.parent_id where t1.del_flag='0' and t1.type='3'/* and t2.type='1' */
AND ( t1.name LIKE CONCAT('%', #{keyword}, '%') or t1.description LIKE CONCAT('%', #{keyword}, '%') or t4.name LIKE CONCAT('%', #{keyword}, '%') or t4.description LIKE CONCAT('%', #{keyword}, '%') )
AND t4.status = #{executeStatus}
AND t1.id = #{objectId}
and t1.time_session_id = #{timeSessionId}
order by t1.create_ts desc
