mapper.select:
SELECT * FROM t_okr_teams ot WHERE 1=1
and ot.owner_id= #{userId}
and ot.type= #{type}

select t1.id, t1.name,t1.type,t1.create_ts
from t_okr_teams t1
join t_okr_team_user_rela t2 on t2.team_id=t1.id
where 1=1

    AND t2.user_id = #{userId}


    AND t1.type = #{type}

select t1.id, t1.name, t1.owner_id, t1.create_ts from t_okr_teams t1 where t1.type='2'
AND t1.owner_id = #{userId}
order by t1.create_ts desc

SELECT COALESCE(COUNT ( DISTINCT t2.* ),0) teamMemberNum, COALESCE(SUM ( t3.duration ),0) totalWorkingHours, COALESCE(COUNT ( DISTINCT t3.task_id ),0) relTaskNum FROM t_okr_teams t1 INNER JOIN t_okr_team_user_rela t2 ON t1.ID = t2.team_id INNER JOIN t_okr_daily t3 ON t2.user_id = t3.report_user_id
and t1.id = #{teamId}
and t3.report_day >=#{queryStartTime}
and t3.report_day <=#{queryEndTime}
