mapper.select:
SELECT distinct t1.id, t1.task_code, t1.task_name, t1.task_status, t1.task_remark, t1.is_deleted, t1.task_start_time, t1.task_end_time, t1.jira_label, t1.confirm_user_id, t1.create_user_id,t2.real_name createUserName, t1.create_ts,t1.update_user_id, t1.update_ts,t1.estimate_time,t3.NAME belongTeamName FROM t_okr_task t1 LEFT join t_okr_sys_user t2 on t1.create_user_id = t2.id left join T_OKR_TEAMS t3 on t1.belong_team = t3.id left join t_okr_task_user_rel t4 on t1.id = t4.task_id
and (t1.task_name LIKE CONCAT('%', #{vo.searchKey }, '%') or t1.task_remark LIKE CONCAT('%', #{vo.searchKey }, '%'))
and t1.belong_team = #{vo.teamId}
AND t1.create_ts >= #{vo.queryStartDate}
AND t1.create_ts <= #{vo.queryEndDate}
t4.user_id = #{vo.currentUserId}
order by t1.id desc
limit #{page.pageSize} offset (#{page.recordStart}-1)

SELECT
  count(distinct t1.*)
FROM t_okr_task t1
LEFT join t_okr_sys_user t2 on t1.create_user_id = t2.id
left join T_OKR_TEAMS t3 on t1.belong_team = t3.id
left join t_okr_task_user_rel t4 on t1.id = t4.task_id


        and (t1.task_name LIKE CONCAT('%', #{vo.searchKey }, '%') or t1.task_remark LIKE CONCAT('%', #{vo.searchKey }, '%'))


        and t1.belong_team = #{vo.teamId}


        AND t1.create_ts >= #{vo.queryStartDate}


        AND t1.create_ts <= #{vo.queryEndDate}



            t4.user_id = #{vo.currentUserId}

SELECT DISTINCT t1.project_id FROM ( SELECT tta.project_id FROM T_OKR_DAILY td INNER JOIN T_OKR_TASK_APPORTION tta ON td.task_id = tta.task_id INNER JOIN t_okr_task TT ON TTA.task_id = TT.ID
and td.report_user_id = #{reportUserId}
and tt.confirm_user_id = #{confirmUserId}
ORDER BY td.report_day DESC ) t1
AND ar.create_ts <= #{vo.queryEndDate}

SELECT
    COUNT ( td.* ) relTaskNum,
    COALESCE ( SUM ( td.DURATION ), 0 ) totalDuration
FROM
    T_OKR_DAILY td
    INNER JOIN t_okr_task_apportion tta ON td.task_id = tta.task_id


       AND td.report_user_id = #{reportUserId}


       AND tta.project_id = #{projectId}


        AND td.report_day >= #{reportStartDate}


        AND td.report_day <= #{reportEndDate}

SELECT ot.id task_id ,ot.task_name ,otur.user_id ,ot2.name team_name ,ot2.id team_id FROM T_OKR_TASK ot left join T_OKR_TASK_USER_REL otur on ot.id = otur.task_id left join T_OKR_TEAMS ot2 on ot2.id = ot.belong_team WHERE 1=1
AND otur.user_id = #{condition.userId}
AND otur.user_id in
#{userId}
AND ot2.id = #{condition.teamId}
AND ot.id = #{condition.taskId}

mapper.sql:
t_okr_task T1
LEFT JOIN t_okr_task_apportion T2 ON T1.ID = T2.task_id
INNER JOIN t_okr_daily t3 on t1.id = t3.task_id

INNER JOIN (
    SELECT DISTINCT
            t3.id, t3.user_name, t3.real_name, t3.photo_url
    FROM
            t_okr_teams t1
            INNER JOIN t_okr_team_user_rela t2 ON t1.ID = t2.team_id
            INNER JOIN t_okr_sys_user t3 ON t2.user_id = t3.ID


            AND t1.owner_id = #{vo.currentUserId}


        and t1.id =#{vo.teamId}


) T4 ON T3.report_user_id = T4.ID

LEFT JOIN T_OKR_TASK_KR_REL t5 on t1.id = t5.task_id



            and t3.report_user_id =#{vo.currentUserId}



        and T2.apportion_name_id =#{vo.productId}


        and T1.id =#{vo.taskId}


        and t2.id =#{vo.categoryId}


        and t5.KR_ID =#{vo.okrId}


        and t3.report_day >=#{vo.queryStartDate}


         and t3.report_day <=#{vo.queryEndDate}
